import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {FieldOption} from "../../entity/FieldOption";
import {FormArray, FormBuilder, FormControl, FormGroup} from "@angular/forms";

@Component({
  selector: 'app-option-list',
  templateUrl: './option-list.component.html',
  styleUrls: ['./option-list.component.css']
})
export class OptionListComponent implements OnInit {
  @Input() fieldOptions: FieldOption[] = [];
  @Input() visible: boolean = false;

  @Output() fieldOptionsChange = new EventEmitter<FieldOption[]>();

  fieldOptionsForm: FormGroup;

  private formBuilder: FormBuilder;

  constructor(formBuilder: FormBuilder) {
    /*this.fieldOptionsForm = new FormGroup({
      options: new FormArray([])
    });*/
    this.formBuilder = formBuilder;

    this.fieldOptionsForm = this.formBuilder.group({
      options: this.formBuilder.array([])
    });
  }

  ngOnInit() {
    this.populateFieldOptionsForm();
  }

  getFormArray(): FormArray {
    return this.fieldOptionsForm.get('optionFormGroup') as FormArray;
  }

  private populateFieldOptionsForm() {
    const optionsArray = this.getFormArray();
    optionsArray.clear();

    if (!this.fieldOptions)
      return;

    /*this.fieldOptions.forEach(option => {
      optionsArray.push(new FormControl(option.text));
    });*/
    /*this.fieldOptions.forEach(option => {
      optionsArray.push(
        this.formBuilder.group({
          text: option.text
        })
      );
    });*/

    this.fieldOptions.forEach(option => {
      const optionFormGroup = new FormGroup({
        text: new FormControl(option.text)
      });
      optionsArray.push(optionFormGroup);
    });
  }

  addFieldOption() {
    const optionsArray = this.getFormArray();
    /*optionsArray.push(new FormControl(''));*/
    optionsArray.push(
      this.formBuilder.group({
        text: ''
      })
    );

    this.updateFieldOptions();
  }

  removeFieldOption(index: number) {
    const optionsArray = this.getFormArray();
    optionsArray.removeAt(index);
    this.updateFieldOptions();
  }

  updateFieldOptions() {
    const optionsArray = this.getFormArray();
    /*this.fieldOptions = optionsArray.value.map((text: string, index: number) => {
      return {
        id: (!this.fieldOptions || this.fieldOptions.length <= index) ? null : this.fieldOptions[index].id,
        text,
        shownName: text
      };
    });*/

    this.fieldOptions = optionsArray.value.map((option: FieldOption) => {
      return {
        id: option.id,
        text: option.text,
        shownName: option.text
      };
    });

    this.fieldOptionsChange.emit(this.fieldOptions);

    console.log('fieldOptions:');
    this.fieldOptions.forEach(option => console.log(option.text + ', ' + option.id));
  }
}
<div *ngIf="visible">
  <div class="options-container">
    <form [formGroup]="fieldOptionsForm">
      <div *ngFor="let optionCtrl of getFormArray().controls; let i = index" class="option-row" formGroupName="options">
        <input type="text" formControlName="{{i}}" class="form-control" placeholder="Вариант {{i + 1}}">
        <button type="button" class="btn btn-danger" (click)="removeFieldOption(i)">Remove</button>
      </div>
    </form>
  </div>
  <button type="button" class="btn btn-primary" (click)="addFieldOption()">Add Option</button>
</div>
